mapper.update:
update user
name=#{setValue.name}, age=#{setValue.age},
WHERE 1=1 and name = #{condition.name}

update user
name=#{name}, age=#{age},
WHERE id=#{id}

mapper.select:
select * from user where 1= 1

	and name = #{name}


	and age = #{age}

 order by id desc

select * from user where 1= 1

	and name = #{name}


	and age = #{age}

 order by id desc

select * from user where 1=1

	and name = #{condition.name}


	and age = #{condition.age}

 order by id desc 
 limit #{offset},#{pagesize}

select count(1) from user where 1=1

	and name = #{name}


	and age = #{age}

select count(1) from user where 1=1

	and name = #{name}


	and age = #{age}

mapper.delete:
delete from user where 1=1
and name=#{name}   
and age =#{age}

delete from user where 1=1
and name=#{name} 
and age =#{age}
